import React from "react";
import { Button, Text, View, Image, TextInput, TouchableOpacity, StyleSheet } from "react-native";

import {useNavigation} from '@react-navigation/native';
export default function Login(){
    const navigation = useNavigation();
    const vermelho = '#BD3804';

    return (
        <View style={styles.container}>
            <View>
                <Image
                    style={styles.imgLogo}
                    source={require('../../../assets/login.png')} 
                />
            </View>

            <TextInput
                style={styles.inputForm}
                placeholder="UsuÃ¡rio"
                autoCorrect={false}
                onChangeText={ () => {} }
            />
            <TextInput
                style={styles.inputForm}
                placeholder="Senha"
                autoCorrect={false}
                onChangeText={ () => {} }
            />

            <TouchableOpacity style={styles.btnSubmitForm}>
                <Text style={styles.txtSubmitForm}>
                    Acessar
                </Text>
            </TouchableOpacity>
            <Text 
                style={styles.linkNewUser}
                onPress={ () => {navigation.navigate('RecoverPassword')} }>
                Esqueceu sua senha?</Text>
            <Text
                style={styles.linkNewUser}
                onPress={ () => {navigation.navigate('NewUser')} }>
                Cadastrar
            </Text>
        </View>
    )
}
const styles = StyleSheet.create({
    
    container: {
        flex: 1, 
        justifyContent: "center", 
        alignItems: "center",
        backgroundColor: '#fff'
    },
    imgLogo: { 
        width: 100,
        height: 100,
        marginBottom: 150
    },
    // login: {
    //     color: "#fff",
    //     marginBottom: 50,
    //     fontSize: 40
    // },

    inputForm:{
        backgroundColor: "#f7f8fc",
        width: "90%",
        marginBottom: 15,
        height: 50,
        borderRadius: 6,
        color:'#BD3804',
        fontSize: 18,
        padding: 10,
    },
    btnSubmitForm:{
        backgroundColor: '#BD3804',
        width: '90%',
        height: 50,
        borderRadius: 6,
        alignItems: 'center',
        justifyContent: 'center',
    },
    txtSubmitForm:{
        color: '#fff',
        fontSize: 22
    },
    linkNewUser:{
        marginTop: 25,
        fontSize: 18,
        color: '#BD3804',
    }
})
